import { useState } from 'react';
import styled, { css } from 'styled-components';
import { ListOrdered, Image as ImageIcon } from 'lucide-react';

const Title = styled.div`
  font-size: 1.5rem;
  font-weight: bold;
  margin-bottom: 0.25rem;
`;

const Subtitle = styled.div`
  font-size: 0.875rem;
  color: #6b7280;
  margin-bottom: 1rem;
`;

const ArtistScroll = styled.div`
  display: flex;
  overflow-x: auto;
  padding-bottom: 0.5rem;
  margin-bottom: 1rem;
  gap: 2rem;
`;

const ArtistItem = styled.div<{ selected: boolean }>`
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  color: ${({ selected }) => (selected ? '#2563eb' : '#6b7280')};
  font-weight: ${({ selected }) => (selected ? 'bold' : 'normal')};
  padding: 0.5rem;
`;

const ArtistImageWrapper = styled.div<{ selected: boolean }>`
  width: 5rem;
  height: 5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 9999px;
  border: 2px solid transparent;
  overflow-y: auto;
  ${({ selected }) =>
    selected &&
    css`
      border: 2px solid #3b82f6;
    `}
  box-shadow: 0px 0px 8px 0px #1447E633;
`;

const ArtistImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 9999px;
`;

const ArtistName = styled.div`
  font-size: 0.75rem;
  margin-top: 0.25rem;
`;

const CardGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 1rem;
  overflow-y: auto;
  max-height: 80vh;

  @media (min-width: 640px) {
    grid-template-columns: repeat(2, 1fr);
  }
`;

const Card = styled.div`
  border-radius: 0.5rem;
  overflow: hidden;
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.1);
`;

const CardImage = styled.img`
  width: 100%;
  height: 12rem;
  object-fit: cover;
`;

const CardContent = styled.div`
  padding: 0.75rem;
`;

const CardTitle = styled.div`
  font-weight: 600;
`;

const CardSubtitle = styled.div`
  font-size: 0.875rem;
  color: #6b7280;
`;

const ListButtonWrapper = styled.div`
  display: flex;
  justify-content: flex-end;
  margin-top: 1rem;
`;

const ListButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 0.375rem;
  background: white;
  color: #111827;
  cursor: pointer;

  &:hover {
    background: #f9fafb;
  }
`;

// List view table 스타일
const TableWrapper = styled.div`
  background-color: #4b5563;
  padding: 1rem;
  border-radius: 0.5rem;
  color: white;
`;

const TableHeader = styled.div`
  display: flex;
  justify-content: space-between;
  font-weight: bold;
  padding-bottom: 0.5rem;
  border-bottom: 1px solid white;
`;

const TableRow = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0.75rem 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
`;

const Tag = styled.span`
  background: transparent;
  border: 1px solid white;
  padding: 0.2rem 0.6rem;
  border-radius: 999px;
  font-size: 0.875rem;
`;

const artists = [
  '멋쟁이 밴드처럼',
  '멋쟁이 호랑이처럼',
  '멋쟁이 호랑이처럼',
  '멋쟁이 호랑이처럼',
  '멋쟁이 호랑이처럼',
  '멋쟁이 호랑이처럼',
  '멋쟁이 호랑이처럼',
  '멋쟁이 호랑이처럼',
  '멋쟁이 호랑이처럼',
  '멋쟁이 사자처럼',
  '멋쟁이 판다처럼',
];

export const LineUp = () => {
  const [selected, setSelected] = useState(0);
  const [isListView, setIsListView] = useState(false);

  return (
    <>
      <Title>아티스트 라인업</Title>
      <Subtitle>올해 축제를 빛낼 아티스트를 지금 바로 확인해보세요.</Subtitle>

      {isListView ? (
        <TableWrapper>
          <TableHeader>
            <div style={{ flex: 2 }}>시간</div>
            <div style={{ flex: 1 }}>카테고리</div>
            <div style={{ flex: 2 }}>공연팀</div>
          </TableHeader>

          {[...Array(7)].map((_, i) => (
            <TableRow key={i}>
              <div style={{ flex: 2 }}>nn:nn - nn:nn</div>
              <div style={{ flex: 1 }}>
                <Tag>{i === 1 ? '댄스' : '밴드'}</Tag>
              </div>
              <div style={{ flex: 2 }}>공연팀명</div>
            </TableRow>
          ))}
        </TableWrapper>
      ) : (
        <>
          <ArtistScroll>
            {artists.map((name, index) => (
              <ArtistItem key={index} selected={selected === index} onClick={() => setSelected(index)}>
                <ArtistImageWrapper selected={selected === index}>
                  <ArtistImage src="images/home/banner/1.png" alt="artist" />
                </ArtistImageWrapper>
                <ArtistName>{name}</ArtistName>
              </ArtistItem>
            ))}
          </ArtistScroll>
          <CardGrid>
            <Card>
              <CardImage src="images/home/banner/1.png" alt="옥씨 부인전" />
              <CardContent>
                <CardTitle>새롭게 재해석한 옥씨 부인전</CardTitle>
                <CardSubtitle>추영우 학우의 꿀 발린 보이스</CardSubtitle>
              </CardContent>
            </Card>
          </CardGrid>
        </>
      )}

      <ListButtonWrapper>
        <ListButton onClick={() => setIsListView((prev) => !prev)}>
          {isListView ? <ImageIcon size={16} /> : <ListOrdered size={16} />}
          {isListView ? '이미지 뷰' : '리스트 뷰'}
        </ListButton>
      </ListButtonWrapper>
    </>
  );
};
