import { useState } from 'react';
import styled from 'styled-components';
import { ChevronLeft, ChevronRight } from 'lucide-react';

export const Banner = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const prevSlide = () => {
    setCurrentIndex((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  const nextSlide = () => {
    setCurrentIndex((prev) => (prev === images.length - 1 ? 0 : prev + 1));
  };

  return (
    <CarouselContainer>
      <TextContainer>
        <Title>본무대 드레스코드 안내</Title>
        <Subtitle>BLUE</Subtitle>
      </TextContainer>
      <Card>
        <CardContent>
          <Image src={images[currentIndex]} alt="Dress Code" />
          <LeftButton onClick={prevSlide}>
            <ChevronLeft size={24} color="#1e3a8a" />
          </LeftButton>
          <RightButton onClick={nextSlide}>
            <ChevronRight size={24} color="#1e3a8a" />
          </RightButton>
        </CardContent>
      </Card>
      <Indicators>
        {images.map((_, index) => (
          <Indicator key={index} active={index === currentIndex} />
        ))}
      </Indicators>
    </CarouselContainer>
  );
};

const images = [
  '/images/home/banner/1.png',
  '/images/home/banner/2.png',
  '/images/home/banner/3.png',
  '/images/home/banner/4.png',
  '/images/home/banner/5.png',
  '/images/home/banner/6.png',
];

const CarouselContainer = styled.div`
  position: relative;
  width: 100%;
  max-width: 520px;
  margin: auto;
  padding: 24px;
  background: linear-gradient(135deg, #e0f2ff, #f0f9ff);
  border-radius: 24px;
`;

const TextContainer = styled.div`
  text-align: left;
  margin-bottom: 12px;
`;

const Title = styled.h2`
  font-size: 20px;
  font-weight: 700;
  color: #1e3a8a;
`;

const Subtitle = styled.h1`
  font-size: 60px;
  font-weight: 900;
  color: rgba(30, 58, 138, 0.1);
  letter-spacing: 4px;
`;

const Card = styled.div`
  overflow: hidden;
  border-radius: 16px;
`;

const CardContent = styled.div`
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Image = styled.img`
  width: 100%;
  height: auto;
  object-fit: contain;
`;

const Button = styled.button`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background: white;
  padding: 8px;
  border-radius: 50%;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
  border: none;
  cursor: pointer;

  &:hover {
    background: #e0e0e0;
  }
`;

const LeftButton = styled(Button)`
  left: 12px;
`;

const RightButton = styled(Button)`
  right: 12px;
`;

const Indicators = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 12px;
`;

interface IndicatorProps {
  active: boolean;
}

const Indicator = styled.div<IndicatorProps>`
  width: 10px;
  height: 10px;
  margin: 0 5px;
  border-radius: 50%;
  background: ${(props) => (props.active ? '#2563eb' : '#d1d5db')};
  transition: all 0.3s ease-in-out;
  transform: ${(props) => (props.active ? 'scale(1.25)' : 'scale(1)')};
`;
