import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { useMenu } from '../context/MenuContext';
import exp from 'constants';

interface TheHeaderProps {
  title: string;
  backgroundColor?: string;
  textColor?: string;
}

export const TheHeader = ({ title, backgroundColor = '#1447e6', textColor = '#000' }: TheHeaderProps) => {
  const navigate = useNavigate();
  const { toggleMenu } = useMenu();

  const openMenu = (event: React.MouseEvent) => {
    event.stopPropagation(); // 부모 Header의 onClick 이벤트 전파 방지
    toggleMenu();
  };

  return (
    <Wrapper>
      <Header onClick={() => navigate('/')}>
        <Icon onClick={openMenu} className="left-icon"></Icon>
        <img style={{ height: '24px' }} src="./images/home/lucaus.webp" alt="logo" />
        <Icon></Icon>
      </Header>
    </Wrapper>
  );
};
export default TheHeader;

const Wrapper = styled.div`
  position: fixed;
  width: 100%;
  top: 0;
  left: 0;
  z-index: 3;
`;

const Header = styled.div`
  // background-color: #1447e6;
  color: white;
  text-align: center;
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 60px;
  padding: 0 3%;
`;

const Icon = styled.div`
  background-size: cover;
  width: 30px;
  height: 30px;

  &.left-icon {
    background-image: url('./images/home/icon-L.webp');
  }

  &:not(.left-icon) {
    background-image: none;
  }
`;
